import React, { useEffect, useRef, useState } from 'react';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import { useChatAPI } from '../hooks/useChatAPI';

const ChatInterface = ({ conversation, onUpdateConversation, isLoading, setIsLoading }) => {
  const { sendMessage, isLoading: apiLoading } = useChatAPI();
  const containerRef = useRef(null);
  const [input, setInput] = useState('');

  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
    }
  }, [conversation?.messages?.length, apiLoading]);

  // Keep external loading state in sync with API hook
  useEffect(() => {
    if (typeof setIsLoading === 'function') {
      setIsLoading(apiLoading);
    }
  }, [apiLoading, setIsLoading]);

  const handleSend = async (text) => {
    if (!text.trim()) return;

    const now = new Date().toISOString();
    const userMessage = {
      id: `${Date.now()}-user`,
      role: 'user',
      content: text,
      timestamp: now
    };

    const updated = {
      ...conversation,
      messages: [...conversation.messages, userMessage]
    };
    onUpdateConversation(updated);
    setInput('');

    try {
      const aiContent = await sendMessage(updated.messages);
      const aiMessage = {
        id: `${Date.now()}-assistant`,
        role: 'assistant',
        content: aiContent,
        timestamp: new Date().toISOString()
      };

      onUpdateConversation({
        ...updated,
        messages: [...updated.messages, aiMessage]
      });
    } catch (e) {
      const errorMessage = {
        id: `${Date.now()}-error`,
        role: 'assistant',
        content: 'Sorry, something went wrong while contacting the AI.',
        timestamp: new Date().toISOString()
      };
      onUpdateConversation({
        ...updated,
        messages: [...updated.messages, errorMessage]
      });
    }
  };

  const handleKeyPress = (e) => {
    if ((e.key === 'Enter' || e.keyCode === 13) && !e.shiftKey) {
      e.preventDefault();
      handleSend(input);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200">
      <div
        ref={containerRef}
        className="p-4 h-[60vh] overflow-y-auto"
      >
        <MessageList messages={conversation.messages} isLoading={apiLoading} />
      </div>

      <div className="p-4 border-t border-gray-200">
        <MessageInput
          value={input}
          onChange={setInput}
          onSend={handleSend}
          onKeyPress={handleKeyPress}
          disabled={apiLoading}
          placeholder="Type your message..."
        />
      </div>
    </div>
  );
};

export default ChatInterface;
