import { useState } from 'react';

export function useChatAPI() {
  const [isLoading, setIsLoading] = useState(false);

  const sendMessage = async (messages) => {
    setIsLoading(true);
    
    try {
      // For demo purposes, we'll use a mock response
      // In production, you would replace this with actual API calls
      
      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1000 + Math.random() * 2000));
      
      // Mock AI responses based on user input
      const lastUserMessage = messages[messages.length - 1];
      const userContent = lastUserMessage.content.toLowerCase();
      
      let response = '';
      
      if (userContent.includes('hello') || userContent.includes('hi')  || userContent.includes('hey')) {
        response = "Hello! How can I assist you today? I'm here to help with any questions or tasks you might have.";
      } else if (userContent.includes('how are you')) {
        response = "I'm doing well, thank you for asking! I'm ready to help you with whatever you need.";
      } else if (userContent.includes('help')) {
        response = "I'm here to help! I can assist with:\n\n- Answering questions\n- Providing information\n- Helping with tasks\n- Having conversations\n\nWhat would you like help with?";
      } else if (userContent.includes('code') || userContent.includes('programming')) {
        response = "I'd be happy to help with programming! Here's a simple example:\n\n```javascript\nfunction greet(name) {\n  return `Hello, ${name}!`;\n}\n\nconsole.log(greet('World')); // Output: Hello, World!\n```\n\nWhat specific programming question do you have?";
      } else if (userContent.includes('weather')) {
        response = "I can't check real-time weather data, but I can help you understand weather concepts or find weather APIs for your projects!";
      } else if (userContent.includes('joke')) {
        response = "Here's a programming joke for you:\n\nWhy do programmers prefer dark mode?\n\nBecause light attracts bugs! ðŸ›ðŸ˜„";
      } else if (userContent.includes('thank')) {
        response = "You're very welcome! I'm glad I could help. Is there anything else you'd like to know or discuss?";
      } else if (userContent.includes('bye') || userContent.includes('goodbye')) {
        response = "Goodbye! It was nice chatting with you. Feel free to come back anytime if you have more questions!";
      } else {
        // Generate a more contextual response based on conversation history
        const conversationContext = messages.slice(-4).map(m => m.content).join(' ');
        
        if (conversationContext.includes('?')) {
          response = "That's an interesting question! I'd be happy to help you explore that topic further. Could you provide more details about what specifically you'd like to know?";
        } else {
          response = "I understand what you're saying. That's a great point to discuss! What would you like to explore or learn more about?";
        }
      }
      
      return response;
      
    } catch (error) {
      console.error('Error in chat API:', error);
      throw new Error('Failed to get response from AI');
    } finally {
      setIsLoading(false);
    }
  };
  // For production use, replace the mock implementation above with this:
  /*
  const sendMessage = async (messages) => {
    try {
      const response = await fetch('/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          messages: messages.map(msg => ({
            role: msg.role,
            content: msg.content
          }))
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to get response from AI');
      }

      const data = await response.json();
      return data.content;
    } catch (error) {
      console.error('Error in chat API:', error);
      throw error;
    }
  };
  */

  return {
    sendMessage,
    isLoading
  };
}
