import React, { useEffect, useRef, useState } from 'react';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import { useChatAPI } from '../hooks/useChatAPI';

const ChatInterface = ({ conversation, onUpdateConversation, isLoading, setIsLoading }) => {
  const { sendMessage, isLoading: apiLoading } = useChatAPI();
  const containerRef = useRef(null);
  const [input, setInput] = useState('');

  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
    }
  }, [conversation?.messages?.length, apiLoading]);

  // Keep external loading state in sync with API hook
  useEffect(() => {
    if (typeof setIsLoading === 'function') {
      setIsLoading(apiLoading);
    }
  }, [apiLoading, setIsLoading]);

  const handleSend = async (text) => {
    if (!text.trim()) return;

    const now = new Date().toISOString();
    const userMessage = {
      id: `${Date.now()}-user`,
      role: 'user',
      content: text,
      timestamp: now
    };

    const updated = {
      ...conversation,
      messages: [...conversation.messages, userMessage]
    };
    onUpdateConversation(updated);
    setInput('');

    try {
      const aiResult = await sendMessage(updated.messages);
      const aiMessage = {
        id: `${Date.now()}-assistant`,
        role: 'assistant',
        content: aiResult.content,
        meta: aiResult.meta,
        timestamp: new Date().toISOString()
      };

      onUpdateConversation({
        ...updated,
        messages: [...updated.messages, aiMessage]
      });
    } catch (e) {
      const errorMessage = {
        id: `${Date.now()}-error`,
        role: 'assistant',
        content: 'Sorry, something went wrong while contacting the AI.',
        timestamp: new Date().toISOString()
      };
      onUpdateConversation({
        ...updated,
        messages: [...updated.messages, errorMessage]
      });
    }
  };

  const handleKeyPress = (e) => {
    if ((e.key === 'Enter' || e.keyCode === 13) && !e.shiftKey) {
      e.preventDefault();
      handleSend(input);
    }
  };

  // Message actions
  const handleDelete = (msg) => {
    const filtered = conversation.messages.filter(m => m.id !== msg.id);
    onUpdateConversation({ ...conversation, messages: filtered });
  };
  const handleLike = (msg) => {
    // You can implement like logic here (e.g., add a liked property or toast)
    alert('You liked this message!');
  };
  const handleCopy = (msg) => {
    navigator.clipboard.writeText(msg.content);
    alert('Message copied!');
  };

  return (
    <div className="bg-gradient-to-br from-blue-50 via-white to-purple-100 rounded-2xl shadow-xl border border-gray-200 max-w-2xl mx-auto my-8 flex flex-col min-h-[70vh]">
      <div className="flex-1 flex flex-col">
        <div
          ref={containerRef}
          className="p-6 h-[60vh] overflow-y-auto custom-scrollbar flex-1"
        >
          <MessageList
            messages={conversation.messages}
            isLoading={apiLoading}
            onDelete={handleDelete}
            onLike={handleLike}
            onCopy={handleCopy}
          />
        </div>
      </div>

      <div className="p-4 border-t border-gray-200 bg-white rounded-b-2xl">
        <MessageInput
          value={input}
          onChange={setInput}
          onSend={handleSend}
          onKeyPress={handleKeyPress}
          disabled={apiLoading}
          placeholder="Type your message..."
        />
      </div>
    </div>
  );
};

export default ChatInterface;
