import { useState } from 'react';

export function useChatAPI() {
  const [isLoading, setIsLoading] = useState(false);

  const sendMessage = async (messages) => {
    setIsLoading(true);
    
    try {
      // For demo purposes, we'll use a mock response
      // In production, you would replace this with actual API calls
      
      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1000 + Math.random() * 2000));
      
      // Mock AI responses based on user input
      const lastUserMessage = messages[messages.length - 1];
      const userContent = lastUserMessage.content;
      const userContentLower = userContent.toLowerCase();

      // Deep analysis: extract keywords, sentiment, and intent
      function extractKeywords(text) {
        return text
          .replace(/[.,!?]/g, '')
          .split(/\s+/)
          .filter(word => word.length > 3)
          .slice(0, 5);
      }
      function detectSentiment(text) {
        const positive = ['good', 'great', 'happy', 'love', 'like', 'enjoy', 'thanks'];
        const negative = ['bad', 'sad', 'hate', 'angry', 'problem', 'issue', 'wrong'];
        let score = 0;
        for (const word of positive) if (text.includes(word)) score++;
        for (const word of negative) if (text.includes(word)) score--;
        if (score > 0) return 'positive';
        if (score < 0) return 'negative';
        return 'neutral';
      }
      function detectIntent(text) {
        if (/\b(help|assist|support)\b/i.test(text)) return 'help';
        if (/\b(joke|funny)\b/i.test(text)) return 'joke';
        if (/\b(code|programming|script)\b/i.test(text)) return 'programming';
        if (/\b(weather|forecast)\b/i.test(text)) return 'weather';
        if (/\b(bye|goodbye|see you)\b/i.test(text)) return 'goodbye';
        if (/\b(hello|hi|hey)\b/i.test(text)) return 'greeting';
        if (/\b(thank|thanks)\b/i.test(text)) return 'gratitude';
        if (text.includes('?')) return 'question';
        return 'statement';
      }

      const keywords = extractKeywords(userContentLower);
      const sentiment = detectSentiment(userContentLower);
      const intent = detectIntent(userContent);

      let response = '';
      let meta = {
        source: 'AI Knowledge Base',
        confidence: Math.floor(80 + Math.random() * 20) + '%',
        extra: '',
        keywords,
        sentiment,
        intent
      };

      if (intent === 'greeting') {
        response = "Hello! How can I assist you today? I'm here to help with any questions or tasks you might have.";
        meta.extra = 'Greeting detected';
      } else if (intent === 'help') {
        response = "I'm here to help! I can assist with:\n\n- Answering questions\n- Providing information\n- Helping with tasks\n- Having conversations\n\nWhat would you like help with?";
        meta.extra = 'Help intent detected';
      } else if (intent === 'programming') {
        response = "I'd be happy to help with programming! Here's a simple example:\n\n```javascript\nfunction greet(name) {\n  return `Hello, ${name}!`;\n}\n\nconsole.log(greet('World')); // Output: Hello, World!\n```\n\nWhat specific programming question do you have?";
        meta.extra = 'Programming topic';
      } else if (intent === 'weather') {
        response = "I can't check real-time weather data, but I can help you understand weather concepts or find weather APIs for your projects!";
        meta.extra = 'Weather topic';
      } else if (intent === 'joke') {
        response = "Here's a programming joke for you:\n\nWhy do programmers prefer dark mode?\n\nBecause light attracts bugs! ðŸ›ðŸ˜„";
        meta.extra = 'Joke requested';
      } else if (intent === 'gratitude') {
        response = "You're very welcome! I'm glad I could help. Is there anything else you'd like to know or discuss?";
        meta.extra = 'Gratitude detected';
      } else if (intent === 'goodbye') {
        response = "Goodbye! It was nice chatting with you. Feel free to come back anytime if you have more questions!";
        meta.extra = 'Goodbye intent';
      } else if (intent === 'question') {
        response = "That's an interesting question! I'd be happy to help you explore that topic further. Could you provide more details about what specifically you'd like to know?";
        meta.extra = 'General question';
      } else {
        // General fallback
        response = "I understand what you're saying. That's a great point to discuss! What would you like to explore or learn more about?";
        meta.extra = 'General discussion';
      }

      // Return both response and meta
      return { content: response, meta };
      
    } catch (error) {
      console.error('Error in chat API:', error);
      throw new Error('Failed to get response from AI');
    } finally {
      setIsLoading(false);
    }
  };
  // For production use, replace the mock implementation above with this:
  /*
  const sendMessage = async (messages) => {
    try {
      const response = await fetch('/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          messages: messages.map(msg => ({
            role: msg.role,
            content: msg.content
          }))
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to get response from AI');
      }

      const data = await response.json();
      return data.content;
    } catch (error) {
      console.error('Error in chat API:', error);
      throw error;
    }
  };
  */

  return {
    sendMessage,
    isLoading
  };
}
